import FA from "react-fontawesome";
import uuidv4 from "uuid";

const Answer = props => (
  <div id={uuidv4()} style={{ paddingBottom: "15px" }}>
    <FA className="has-text-primary" name="angle-double-right" />
    <span style={{ paddingLeft: "5px" }}> {props.text}</span>
    <p style={{ paddingLeft: "18px" }} className="has-text-grey">
      {props.translated}
      <FA name="external-link-alt" />
    </p>
  </div>
);

export default Answer;
