import HeaderImports from "../common/HeaderImports";
import Navbar from "./Navbar";
import Footer from "./Footer";
import ReactGA from "react-ga";
import Head from "next/head";
import LazyLoad from "react-lazyload";

class Layout extends React.Component {
  componentDidMount() {
    ReactGA.initialize("UA-146229442-1");
    ReactGA.pageview(window.location.pathname + window.location.search);
  }

  render() {
    return (
      <div>
        <HeaderImports />
        <Navbar />

        <br />
        <div className={this.props.full ? "container-fluid" : "container"}>
          {this.props.children}
        </div>
        <br />
        <Footer />

        <style jsx>{`
          * {
            background-color: #efeeec;
          }

          .black {
            color: black !important;
          }

          .styled-ul li {
            list-style: disc !important;
            padding-bottom: 1.5em;
          }

          .container {
            width: 100%;
            padding-right: 15px;
            padding-left: 15px;
            margin-right: auto;
            margin-left: auto;
          }

          @media (min-width: 576px) {
            .container {
              max-width: 540px;
            }
          }

          @media (min-width: 768px) {
            .container {
              max-width: 720px;
            }
          }

          @media (min-width: 992px) {
            .container {
              max-width: 960px;
            }
          }

          @media (min-width: 1200px) {
            .container {
              max-width: 1140px;
            }
          }

          .rounded {
            border-radius: 15px;
            border: 1px solid #eee;
            padding-left: 20px;
            padding-top: 20px;
          }

          .hidden {
            display: none !important;
          }

          .hidden * {
            display: none !important;
          }

          .answers p {
            color: black !important;
          }

          .underline:hover {
            text-decoration: underline !important;
          }

          caption {
            padding-top: 0.75rem;
            padding-bottom: 0.75rem;
            color: #6c757d;
            text-align: left;
            caption-side: bottom;
          }

          .scrollable {
            overflow: auto !important;
            height: 100px !important;
          }

          .rainbow-text {
            background-image: linear-gradient(
              to left,
              violet,
              indigo,
              blue,
              green,
              yellow,
              orange,
              red
            );
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
          }

          .shadow-sm {
            box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075) !important;
          }

          .shadow {
            box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
          }

          .shadow-lg {
            box-shadow: 0 1rem 3rem rgba(0, 0, 0, 0.175) !important;
          }

          .shadow-none {
            box-shadow: none !important;
          }

          .list-unstyled {
            padding-top: 10px;
            list-style: none !important;
            margin: 0px !important;
          }

          .list-unstyled li {
            margin-bottom: 13px;
          }

          /* Navigation icon padding */
          .nav-icon-1 {
            padding-right: 1px;
          }

          .nav-icon-2 {
            padding-right: 2px;
          }

          .nav-icon-3 {
            padding-right: 3px;
          }

          .nav-icon-4 {
            padding-right: 4px;
          }

          .nav-icon-5 {
            padding-right: 5px;
          }

          .nav-icon-6 {
            padding-right: 6px;
          }

          .nav-icon-7 {
            padding-right: 7px;
          }

          .nav-icon-8 {
            padding-right: 8px;
          }

          .nav-icon-9 {
            padding-right: 9px;
          }

          .nav-icon-10 {
            padding-right: 10px;
          }

          .nav-icon-11 {
            padding-right: 11px;
          }

          .nav-icon-12 {
            padding-right: 12px;
          }

          .nav-icon-13 {
            padding-right: 13px;
          }

          .nav-icon-14 {
            padding-right: 14px;
          }

          .nav-icon-15 {
            padding-right: 15px;
          }

          .nav-icon-17 {
            padding-right: 17px;
          }

          /* Master.css */

          .fa-linkedin-in {
            display: none !important;
          }

          p,
          span,
          li {
            line-height: 20px;
          }

          .sticky {
            position: fixed;
            top: 0;
            width: 100%;
          }

          /* Add some top padding to the page content to prevent sudden quick movement (as the header gets a new position at the top of the page (position:fixed and top:0) */
          .sticky + .content {
            padding-top: 102px;
          }

          .inline-icon {
            margin-right: 8px;
          }

          .nav-icon {
            margin-right: 5px;
          }

          .breadcrumb {
            background: transparent !important;
          }

          /* Index.css */

          .nav-tabs > li.active > a,
          .nav-tabs > li.active > a:focus,
          .nav-tabs > li.active > a:hover {
            border: 0 !important;
          }

          .navbar-item.is-mega {
            position: static;
          }

          .is-mega-menu-title {
            margin-bottom: 0;
            padding: 0.375rem 1rem;
          }

          .push-down {
            margin-top: 5px;
          }

          .translation {
            color: #0d0314;
            background-color: #eeeeee;
            width: 100%;
            padding: 10px;
            margin: 5px;
          }

          .menu-link:hover {
            color: #8a3ab9;
          }

          .menu-link {
            color: #209cee !important;
          }

          .my-blue {
            color: #209cee !important;
          }

          .my-blue:hover {
            text-decoration: none;
            color: #00d8ff !important;
          }

          .my-blue-on-hover:hover {
            color: #209cee !important;
          }

          .support-text {
            color: grey;
          }

          .facebook-blue {
            color: #3b5998 !important;
          }

          .blink {
            animation-duration: 4000ms;
            animation-name: blink;
            animation-iteration-count: infinite;
            animation-direction: alternate;
          }

          @keyframes blink {
            from {
              opacity: 1;
            }

            to {
              opacity: 0;
            }
          }

          .loader,
          .loader:before,
          .loader:after {
            border-radius: 50%;
            width: 2.5em;
            height: 2.5em;
            animation-fill-mode: both;
            animation: load7 1.8s infinite ease-in-out;
          }

          .loader {
            color: #ffffff;
            font-size: 10px;
            margin: 80px auto;
            position: relative;
            text-indent: -9999em;
            transform: translateZ(0);
            animation-delay: -0.16s;
          }

          .loader:before,
          .loader:after {
            content: "";
            position: absolute;
            top: 0;
          }

          .loader:before {
            left: -3.5em;
            animation-delay: -0.32s;
          }

          .loader:after {
            left: 3.5em;
          }

          @keyframes load7 {
            0%,
            80%,
            100% {
              box-shadow: 0 2.5em 0 -1.3em;
            }

            40% {
              box-shadow: 0 2.5em 0 0;
            }
          }

          .midnight-blue {
            color: #003366;
          }

          /* https://www.w3schools.com/howto/howto_css_zoom_hover.asp */
          .zoom {
            padding: 50px;
            background-color: green;
            transition: transform 0.2s;
            /* Animation */
            width: 200px;
            height: 200px;
            margin: 0 auto;
          }

          .zoom:hover {
            transform: scale(1.5);
            /* (150% zoom - Note: if the zoom is too large, it will go outside of the viewport) */
          }

          /* MAKE YOUTUBE VIDEOS RESPONSIVE - https://avexdesigns.com/responsive-youtube-embed/  */
          .video-container {
            position: relative;
            padding-bottom: 56.25%;
            padding-top: 30px;
            height: 0;
            overflow: hidden;
          }

          .video-container iframe,
          .video-container object,
          .video-container embed {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
          }

          @media (max-width: 767px) {
            .text-sm-center {
              text-align: center !important;
            }
          }

          /* 6fb9f5 is a very nice color */

          .remove-btn {
            padding: 0px !important;
            border: none !important;
            background: none !important;
          }

          .spin {
            -webkit-animation: spin 4s infinite linear;
          }

          @-webkit-keyframes spin {
            0% {
              -webkit-transform: rotate(0deg);
            }

            100% {
              -webkit-transform: rotate(360deg);
            }
          }

          footer {
            color: #c9c9c9 !important;
          }

          footer a {
            color: #c9c9c9 !important;
          }

          footer a:hover {
            color: white !important;
          }

          .u-navbar {
            border-bottom: 1px solid #bbbbbb;
          }

          .u-nav-link {
            text-decoration: none;
            padding: 10px 0px;
            margin: 0px 10px;
            display: inline-block;
            position: relative;
            opacity: 0.75;
          }

          .u-nav-link:hover {
            opacity: 1;
          }

          .u-nav-link::before {
            transition: 300ms;
            height: 5px;
            content: "";
            position: absolute;
            background-color: #031d44;
          }

          .u-nav-link-ltr::before {
            width: 0%;
            bottom: 10px;
          }

          .u-nav-link-ltr:hover::before {
            width: 100%;
          }

          .u-nav-link-fade-up::before {
            width: 100%;
            bottom: 5px;
            opacity: 0;
          }

