import uuid4 from "uuid";

const PaperView = props => (
  <div>
    <span />
    <a download href={props.href}>
      <img className="image preview-image" alt="past paper" src={props.src} />
    </a>
    <style>{`
      @media only screen and (min-width: 600px) {
        .preview-image {
          max-height: 200px;
          max-width: 120px;
          padding-right: "15px"
        }
      }

      @media only screen and (max-width: 600px) {
        .preview-image {
          max-height: 170px;
          max-width: 100px;
          padding-right: "15px"
        }
      }
      `}</style>
  </div>
);

export default PaperView;
