import uuidv4 from "uuid";
import LazyLoad from "react-lazyload";
import { Animated } from "react-animated-css";
import Img from "react-image";
import Loader from "react-loader-spinner";

// Assets
import CSS from "bulma-carousel/dist/css/bulma-carousel.min.css";
import bulmaCarousel from "bulma-carousel/dist/js/bulma-carousel.js";

import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

class Carousel extends React.Component {
  componentDidMount() {
    bulmaCarousel.attach("#carousel-home", {
      slidesToScroll: 1,
      slidesToShow: 1
    });

    // To access to bulmaCarousel instance of an element
    const element = document.querySelector("#carousel-demo");
    if (element && element.bulmaCarousel) {
      // bulmaCarousel instance is available as element.bulmaCarousel
    }
  }
  render() {
    return (
      <div>
        <section class="hero is-medium has-carousel">
          <div id="carousel-home" className="hero-carousel">
            <div className="item-1">
              <Img
                src={["", "", ""]}
                alt=""
                loader={
                  <Loader
                    type="ThreeDots"
                    color="#00BFFF"
                    height={100}
                    width={100}
                    timeout={3000} //3 secs
                  />
                }
              />
            </div>
            <div className="item-2">
              <Img
                src={["", "", ""]}
                alt=""
                loader={
                  <Loader
                    type="ThreeDots"
                    color="#00BFFF"
                    height={100}
                    width={100}
                    timeout={3000} //3 secs
                  />
                }
              />
            </div>
            <div className="item-3">
              <Img
                src={["", "", ""]}
                alt=""
                loader={
                  <Loader
                    type="ThreeDots"
                    color="#00BFFF"
                    height={100}
                    width={100}
                    timeout={3000} //3 secs
                  />
                }
              />
              <Footer />
            </div>
          </div>
          <div className="hero-head"></div>
          <div className="hero-body"></div>
          <div className="hero-foot"></div>
        </section>
        <br />
      </div>
    );
  }
}

export default Carousel;
