import HeaderImports from "../common/HeaderImports";
import Navbar from "./Navbar";
import Footer from "./Footer";
import ReactGA from "react-ga";
import Head from "next/head";
import LazyLoad from "react-lazyload";

// CSS imports
import CSS from "../assets/bulma.css";
import BulmaAccordianCSS from "../assets/bulma-accordian.css";
import "../assets/fontawesome.js";

class Layout extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isMobile: false
    };
  }

  componentWillMount() {}

  componentDidMount() {
    ReactGA.initialize("UA-146229442-1");
    ReactGA.pageview(window.location.pathname + window.location.search);
  }

  render() {
    return (
      <div className="app">
        <HeaderImports />
        <Navbar />

        <br />
        <main role="main" id="main">
          <div className={this.props.full ? "container-fluid" : "container"}>
            {this.props.children}
          </div>
        </main>
        <br />
        <Footer />

        <style jsx>{`
          * {
            box-sizing: border-box;
          }

          .list-ul {
            padding-right: 20px;
          }

          .slider {
            width: 300px;
            text-align: center;
            overflow: hidden;
          }

          .slides {
            display: flex;

            overflow-x: auto;
            scroll-snap-type: x mandatory;

            scroll-behavior: smooth;
            -webkit-overflow-scrolling: touch;
          }
          .slides::-webkit-scrollbar {
            width: 10px;
            height: 10px;
          }
          .slides::-webkit-scrollbar-thumb {
            background: black;
            border-radius: 10px;
          }
          .slides::-webkit-scrollbar-track {
            background: transparent;
          }
          .slides > div {
            scroll-snap-align: start;
            flex-shrink: 0;
            width: 300px;
            height: 300px;
            margin-right: 50px;
            border-radius: 10px;
            background: #eee;
            transform-origin: center center;
            transform: scale(1);
            transition: transform 0.5s;
            position: relative;

            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 100px;
          }
          .slides > div:target {
            /*   transform: scale(0.8); */
          }
          .author-info {
            background: rgba(0, 0, 0, 0.75);
            color: white;
            padding: 0.75rem;
            text-align: center;
            position: absolute;
            bottom: 0;
            left: 0;
            width: 100%;
            margin: 0;
          }
          .author-info a {
            color: white;
          }
          img {
            object-fit: cover;
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
          }

          .slider > a {
            display: inline-flex;
            width: 1.5rem;
            height: 1.5rem;
            background: white;
            text-decoration: none;
            align-items: center;
            justify-content: center;
            border-radius: 50%;
            margin: 0 0 0.5rem 0;
            position: relative;
          }
          .slider > a:active {
            top: 1px;
          }
          .slider > a:focus {
            background: #000;
          }

          /* Don't need button navigation */
          @supports (scroll-snap-type) {
            .slider > a {
              display: none;
            }
          }

          html,
          body {
            height: 100%;
            overflow: hidden;
          }
          body {
            display: flex;
            align-items: center;
            justify-content: center;
            background: linear-gradient(to bottom, #74abe2, #5563de);
            font-family: "Ropa Sans", sans-serif;
          }

          * {
            background-color: #f6f8fa;
          }

          .black {
            color: black !important;
          }

          .styled-ul li {
            list-style: disc !important;
            padding-bottom: 1.5em;
          }

          .container {
            width: 100%;
            padding-right: 15px;
            padding-left: 15px;
            margin-right: auto;
            margin-left: auto;
          }

          @media (min-width: 576px) {
            .container {
              max-width: 540px;
            }
          }

          @media (min-width: 768px) {
            .container {
              max-width: 720px;
            }
          }

          @media (min-width: 992px) {
            .container {
              max-width: 960px;
            }
          }

          @media (min-width: 1200px) {
            .container {
              max-width: 1140px;
            }
          }

          .rounded {
            border-radius: 15px;
            border: 1px solid #eee;
            padding-left: 20px;
            padding-top: 20px;
          }

          .hidden {
            display: none !important;
          }

          .hidden * {
            display: none !important;
          }

          .underline:hover {
            text-decoration: underline !important;
          }

          caption {
            padding-top: 0.75rem;
            padding-bottom: 0.75rem;
            color: #6c757d;
            text-align: left;
            caption-side: bottom;
          }

          .scrollable {
            overflow: auto !important;
            height: 100px !important;
          }

          .rainbow-text {
            background-image: linear-gradient(
              to left,
              violet,
              indigo,
              blue,
              green,
              yellow,
              orange,
              red
            );
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
          }

          .shadow-sm {
            box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075) !important;
          }

          .shadow {
            box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
          }

          .shadow-lg {
            box-shadow: 0 1rem 3rem rgba(0, 0, 0, 0.175) !important;
          }

          .shadow-none {
            box-shadow: none !important;
          }

          .list-unstyled {
            padding-top: 10px;
            list-style: none !important;
            margin: 0px !important;
          }

          .list-unstyled li {
            margin-bottom: 13px;
          }

          /* Navigation icon padding */
          .nav-icon-1 {
            padding-right: 1px;
          }

          .nav-icon-2 {
            padding-right: 2px;
          }

          .nav-icon-3 {
            padding-right: 3px;
          }

          .nav-icon-4 {
            padding-right: 4px;
          }

          .nav-icon-5 {
            padding-right: 5px;
          }

          .nav-icon-6 {
            padding-right: 6px;
          }

          .nav-icon-7 {
            padding-right: 7px;
          }

          .nav-icon-8 {
            padding-right: 8px;
          }

          .nav-icon-9 {
            padding-right: 9px;
          }

          .nav-icon-10 {
            padding-right: 10px;
          }

          .nav-icon-11 {
            padding-right: 11px;
          }

          .nav-icon-12 {
            padding-right: 12px;
          }

          .nav-icon-13 {
            padding-right: 13px;
          }

          .nav-icon-14 {
            padding-right: 14px;
          }

          .nav-icon-15 {
            padding-right: 15px;
          }

          .nav-icon-17 {
            padding-right: 17px;
          }

          /* Master.css */

          .fa-linkedin-in {
            display: none !important;
          }

          p,
          span,
          li {
            line-height: 20px;
          }

          .sticky {
            position: fixed;
            top: 0;
            width: 100%;
          }

          /* Add some top padding to the page content to prevent sudden quick movement (as the header gets a new position at the top of the page (position:fixed and top:0) */
          .sticky + .content {
            padding-top: 102px;
          }

          .inline-icon {
            margin-right: 8px;
          }

          .nav-icon {
            margin-right: 5px;
          }

          .breadcrumb {
            background: transparent !important;
          }

          /* Index.css */

          .nav-tabs > li.active > a,
          .nav-tabs > li.active > a:focus,
          .nav-tabs > li.active > a:hover {
            border: 0 !important;
          }

          .navbar-item.is-mega {
            position: static;
          }

          .is-mega-menu-title {
            margin-bottom: 0;
            padding: 0.375rem 1rem;
          }

          .push-down {
            margin-top: 5px;
          }

          .translation {
            color: #0d0314;
            background-color: #eeeeee;
            width: 100%;
            padding: 10px;
            margin: 5px;
          }

          .menu-link:hover {
            color: #8a3ab9;
          }

          .menu-link {
            color: #209cee !important;
          }

          .my-blue {
            color: #209cee !important;
          }

          .my-blue:hover {
            text-decoration: none;
            color: #00d8ff !important;
          }

          .my-blue-on-hover:hover {
            color: #209cee !important;
          }

          .support-text {
            color: grey;
          }

          .facebook-blue {
            color: #3b5998 !important;
          }

          .blink {
            animation-duration: 4000ms;
            animation-name: blink;
            animation-iteration-count: infinite;
            animation-direction: alternate;
          }

          @keyframes blink {
            from {
              opacity: 1;
            }

            to {
              opacity: 0;
            }
          }

          .loader,
          .loader:before,
          .loader:after {
            border-radius: 50%;
            width: 2.5em;
            height: 2.5em;
            animation-fill-mode: both;
            animation: load7 1.8s infinite ease-in-out;
          }

          .loader {
            color: #ffffff;
