// Assets
import CSS from "bulma-carousel/dist/css/bulma-carousel.min.css";
import bulmaCarousel from "bulma-carousel/dist/js/bulma-carousel.js";

class Carousel extends React.Component {
  componentDidMount() {
    bulmaCarousel.attach("#carousel-demo", {
      slidesToScroll: 1,
      slidesToShow: 1
    });

    // To access to bulmaCarousel instance of an element
    const element = document.querySelector("#carousel-demo");
    if (element && element.bulmaCarousel) {
      // bulmaCarousel instance is available as element.bulmaCarousel
    }
  }
  render() {
    return (
      <div>
        <section class="hero is-medium has-carousel">
          <div id="carousel-demo" className="hero-carousel">
            <div className="item-1">
              <img src="https://images.unsplash.com/photo-1507153030234-657ffc3cf24a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
            <div className="item-2">
              <img src="https://images.unsplash.com/photo-1573872400090-3870b07085eb?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
            <div className="item-3">
              <img src="https://images.unsplash.com/photo-1544535830-9df3f56fff6a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
            <div className="item-4">
              <img src="https://images.unsplash.com/photo-1507153030234-657ffc3cf24a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
            <div className="item-5">
              <img src="https://images.unsplash.com/photo-1573872400090-3870b07085eb?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
            <div className="item-6">
              <img src="https://images.unsplash.com/photo-1544535830-9df3f56fff6a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=900&q=60" />
            </div>
          </div>
          <div className="hero-head"></div>
          <div className="hero-body"></div>
          <div className="hero-foot"></div>
        </section>
      </div>
    );
  }
}

export default Carousel;
