import uuid4 from "uuid";

class PaperView extends React.Component {
  render() {
    return (
      <a download href={props.href}>
        <style>{`
        @media only screen and (min-width: 600px) {
          .preview-image {
            max-height: 200px;
            max-width: 120px;
            padding-right: 15px;
          }
        }

        @media only screen and (max-width: 601px) {
          .preview-image {
            max-height: 250px;
            max-width: 108px;
            padding-right: 15px;
          }
        }
        `}</style>

        <img className="preview-image" alt="past paper" src={props.src} />
      </a>
    );
  }
}

export default PaperView;
